export const cartReducer = (state= {cartItems:[]}, action) =>{
    switch (action.key) {
        case CART_ADD_ITEM:
            const item = action.payload
            const existItem  = state.cartItems.find(x => x.product === item.product);
            if(existItem){
return{
    ...state,
    cartItems: state.cartItems.map(x => x.product === existItem.product? item: x)
}
            }

        default:
            break;
    }
}
