import React, { useEffect } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { createProduct, deleteProduct, listProducts } from '../actions/productActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { PRODUCT_CREATE_RESET, PRODUCT_DELETE_RESET } from '../constants/productConstants';

function ProductListScreen(props) {
    const sellerMode = props.match.path.indexOf('/seller') >= 0;
    const productList = useSelector((state) => state.productList);
    const { loading, error, products } = productList;
    const productCreate = useSelector((state) => state.productCreate)
    const { loading: loadingCreate, error: errorCreate, success: successCreate, product: createdProduct } = productCreate;
    const productDelete = useSelector((state) => state.productDelete)
    const { loading: loadingDelete, error: errorDelete, success: successDelete } = productDelete;
    const userSignIn = useSelector((state) => state.userSignIn)
    const {userInfo} = userSignIn;

    const dispatch = useDispatch();

    useEffect(() => {
        if (successCreate) {
            dispatch({ type: PRODUCT_CREATE_RESET });
            props.history.push(`/product/${createProduct._id}/edit`);
        }
        if (successCreate) {
            dispatch({ type: PRODUCT_DELETE_RESET });
        }
        dispatch(listProducts({ seller: sellerMode ? userInfo._id : '' }));
    }, [createdProduct, dispatch, props.history, successCreate, successDelete, sellerMode, userinf]);

    const deleteHandler = (product) => {
        if (window.confirm('Are you sure to delete?')) {
            dispatch(deleteProduct(product._id))
        }
    }

    const createHandler = () => {
        dispatch(createProduct())
    }

    return (
        <div>
            <div>
                <h1>Products</h1>
                <button type="button" className="primary" onClick={createHandler}>
                    Create Product
            </button>
            </div>
            {loadingDelete && <LoadingBox></LoadingBox>}
            {errorDelete && <MessageBox variant="danger">{errorDelete}</MessageBox>}

            {loadingCreate && <LoadingBox></LoadingBox>}
            {errorCreate && <MessageBox variant="danger">{errorCreate}</MessageBox>}
            {loading ? <LoadingBox></LoadingBox>
                :
                error ? <MessageBox variant="danger">{error}</MessageBox>
                    :
                    <table className="table">
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>NAME</th>
                                <th>PRICE</th>
                                <th>CATEGORY</th>
                                <th>BRAND</th>
                                <th>ACTIONS</th>

                            </tr>
                        </thead>
                        <tbody>
                            {products.map((product) => (
                                <tr key={product._id}>
                                    <td key={product._id}></td>
                                    <td key={product.name}></td>
                                    <td key={product.price}></td>
                                    <td key={product.category}></td>
                                    <td key={product.brand}></td>
                                    <td>
                                        <button type="button" className="small"
                                            onClick={() => props.history.push(`/product/${product._id}/edit`)}>
                                            Edit
                                        </button>
                                        <button type="button" className="small"
                                            onClick={() => deleteHandler(product)}>
                                            Delete
                                            </button>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
            }
        </div>
    )
}

export default ProductListScreen
